import React from 'react';


export default function separateSymbols(text) {
    let manaCost = text.split("{").map(element => element = "{" + element)
    manaCost.shift();
    let manaWithSymbols = manaCost.map(mana => addSymbols(mana))
    return manaWithSymbols
}

function addSymbols(cost) {
    switch (cost) {
        case "{W}":
            return "../images/w.svg"
            break;
        case "{U}":
            return "../images/u.svg"
            break;
        case "{B}":
            return "../images/b.svg"
            break;
        case "{R}":
            return "../images/r.svg"
            break;
        case "{G}":
            return "../images/g.svg"
            break;
        default:
            return <p>{cost}</p>
    }
}
