import React from 'react';
import xMana from "../images/mana symbols/x.svg";
import yMana from "../images/mana symbols/y.svg";
import zMana from "../images/mana symbols/z.svg";
import whiteOrBlue from "../images/mana symbols/wu.svg";
import whiteOrBlack from "../images/mana symbols/wb.svg";
import blackOrRed from "../images/mana symbols/br.svg";
import blackOrGreen from "../images/mana symbols/bg.svg";
import blueOrBlack from "../images/mana symbols/ub.svg";
import blueOrRed from "../images/mana symbols/ur.svg";
import redOrGreen from "../images/mana symbols/rg.svg";
import redOrWhite from "../images/mana symbols/rw.svg";
import greenOrWhite from "../images/mana symbols/gw.svg";
import greenOrBlue from "../images/mana symbols/gu.svg";
import twoOrWhite from "../images/mana symbols/2w.svg";
import twoOrBlue from "../images/mana symbols/2u.svg";
import twoOrBlack from "../images/mana symbols/2b.svg";
import twoOrRed from "../images/mana symbols/2r.svg";
import twoOrGreen from "../images/mana symbols/2g.svg";
import colorOrLife from "../images/mana symbols/p.svg";
import whiteOrLife from "../images/mana symbols/wp.svg";
import blueOrLife from "../images/mana symbols/up.svg";
import blackOrLife from "../images/mana symbols/bp.svg";
import redOrLife from "../images/mana symbols/rp.svg";
import greenOrLife from "../images/mana symbols/gp.svg";
import halfWhite from "../images/mana symbols/hw.svg";
import halfRed from "../images/mana symbols/hr.svg";
import whiteMana from "../images/mana symbols/w.svg";
import blueMana from "../images/mana symbols/u.svg";
import blackMana from "../images/mana symbols/b.svg";
import redMana from "../images/mana symbols/r.svg";
import greenMana from "../images/mana symbols/g.svg";
import colorlessMana from "../images/mana symbols/c.svg";
import snowMana from "../images/mana symbols/s.svg";



export default function converText(manaText) {
    let manaCostArray = manaText
        .split("{")
        .map(element => "{" + element)
        .filter(element => 1 < element.length);
    let symbolCost = manaCostArray.map(mana => findSymbol(mana))

    return symbolCost

}

function removeBrackets(text) {
    var mana = text.split("{")[1].split("}")[0];
    return mana;
}

function findSymbol(mana) {
    switch (mana) {
        case "{U}":
            return (<img className="manaSymbol" src={blueMana} alt="" />)
            break;
        case "{B}":
            return (<img className="manaSymbol" src={blackMana} alt="" />)
            break;
        case "{G}":
            return (<img className="manaSymbol" src={greenMana} alt="" />)
            break;
        case "{R}":
            return (<img className="manaSymbol" src={redMana} alt="" />)
            break;
        case "{W}":
            return (<img className="manaSymbol" src={whiteMana} alt="" />)
            break;


            return (<img className="manaSymbol" src={xMana} alt="" />)
            break;
            return (<img className="manaSymbol" src={yMana} alt="" />)
            break;
            return (<img className="manaSymbol" src={zMana} alt="" />)
            break;
            return (<img className="manaSymbol" src={whiteOrBlue} alt="" />)
            break;
            return (<img className="manaSymbol" src={whiteOrBlack} alt="" />)
            break;
            return (<img className="manaSymbol" src={blackOrRed} alt="" />)
            break;
            return (<img className="manaSymbol" src={blackOrGreen} alt="" />)
            break;
            return (<img className="manaSymbol" src={blueOrBlack} alt="" />)
            break;
            return (<img className="manaSymbol" src={blueOrRed} alt="" />)
            break;
            return (<img className="manaSymbol" src={redOrGreen} alt="" />)
            break;
            return (<img className="manaSymbol" src={redOrWhite} alt="" />)
            break;
            return (<img className="manaSymbol" src={greenOrWhite} alt="" />)
            break;
            return (<img className="manaSymbol" src={greenOrBlue} alt="" />)
            break;
            return (<img className="manaSymbol" src={twoOrWhite} alt="" />)
            break;
            return (<img className="manaSymbol" src={twoOrBlue} alt="" />)
            break;
            return (<img className="manaSymbol" src={twoOrBlack} alt="" />)
            break;
            return (<img className="manaSymbol" src={twoOrRed} alt="" />)
            break;
            return (<img className="manaSymbol" src={twoOrGreen} alt="" />)
            break;
            return (<img className="manaSymbol" src={colorOrLife} alt="" />)
            break;
            return (<img className="manaSymbol" src={whiteOrLife} alt="" />)
            break;
            return (<img className="manaSymbol" src={blueOrLife} alt="" />)
            break;
            return (<img className="manaSymbol" src={blackOrLife} alt="" />)
            break;
            return (<img className="manaSymbol" src={redOrLife} alt="" />)
            break;
            return (<img className="manaSymbol" src={greenOrLife} alt="" />)
            break;
            return (<img className="manaSymbol" src={halfWhite} alt="" />)
            break;
            return (<img className="manaSymbol" src={halfRed} alt="" />)
            break;
            return (<img className="manaSymbol" src={whiteMana} alt="" />)
            break;
            return (<img className="manaSymbol" src={blueMana} alt="" />)
            break;
            return (<img className="manaSymbol" src={blackMana} alt="" />)
            break;
            return (<img className="manaSymbol" src={redMana} alt="" />)
            break;
            return (<img className="manaSymbol" src={greenMana} alt="" />)
            break;
            return (<img className="manaSymbol" src={colorlessMana} alt="" />)
            break;
            return (<img className="manaSymbol" src={snowMana} alt="" />)


















        default:
            return (<span>{removeBrackets(mana)}</span>)

    }
}
