import React from 'react';
import { Container, Col, Input, Form, InputGroup, InputGroupAddon, Button, Row, Modal, ModalBody } from 'reactstrap';
import '../App.css';

function SearchBox(props) {
    return (
        <Row className="searchBox">
            <Col xs="12">
                <Form className="inputBox" onChange={props.handleSearchInputChange} onSubmit={props.handleSearch}>
                    <InputGroup  >
                        <Input />
                        <InputGroupAddon addonType="append"><Button color="secondary">Search</Button></InputGroupAddon>
                    </InputGroup>
                </Form>
            </Col>
        </Row>
    )
}

function CardDataModal(props) {
    return (
        <Modal isOpen={props.cardSearchState.modalState} toggle={event => props.removeModal()} >
            <ModalHeader toggle={event => props.removeModal()}>{props.cardSearchState.selectedCard.name}</ModalHeader>
            <ModalBody>

            </ModalBody>
        </Modal>
    )
}

function Card(props) {
    return (
        <Col xs="6" md="3" className="searchResultsCard">
            <Button onClick={event => props.toggleSearchModal(props.card)}>
                <img src={props.card.image_uris.border_crop} style={{ width: "100%" }} />
            </Button>
        </Col>
    )
}




export default function CardSearch(props) {
    return (
        <Container className="CardSearchWrapper">
            <SearchBox handleSearchInputChange={props.handleSearchInputChange} handleSearch={props.handleSearch} />
            <Row>
                {props.cardSearchState.cardList.map((card, idx) => <Card key={idx} card={card} toggleSearchModal={props.toggleSearchModal} />)}
            </Row>
            <CardDataModal cardSearchState={props.cardSearchState} removeModal={props.removeModal} />
        </Container>

    )
}
