import React from 'react';
import { Container, Card, CardBody, CardTitle, CardSubtitle, CardText, CardHeader, Col, Input, Form, InputGroup, InputGroupAddon, Button, Row, Modal, ModalBody, ModalHeader, ModalFooter } from 'reactstrap';
import '../App.css';

function PlayerCard(props) {
    return (
        <Col xs="6" md="4" style={{ paddingLeft: "0.5vh", paddingRight: "0.5vh" }}>
            <Card className="playerCard" style={{ borderRadius: '0' }}>
                <CardHeader><input className="playerName" placeholder={"Player " + props.playerNumber} /></CardHeader>
                <CardBody>
                    {props.player.map((player, idx) => <CardBody tag="h3"><Button onClick={event => props.plusOne(props.idx, idx)} />{player}<Button onClick={event => props.minusOne(props.idx, idx)} /></CardBody>)}
                </CardBody>
                <Button onClick={event => props.addCounter(props.idx)} style={{ borderRadius: '0' }}>Add Counter</Button>
            </Card>
        </Col>
    )
}

export default function BattleCounter(props) {
    return (
        <Container>
            <Button onClick={event => props.addPlayer()}>Add Player</Button>
            <Button onClick={event => props.removeAllPlayers()}>Remove All Players</Button>

            <Row>
                {props.battleCounterState.players.map((player, idx) => <PlayerCard player={player} playerNumber={idx + 1} idx={idx} addCounter={props.addCounter} plusOne={props.plusOne} minusOne={props.minusOne} />)}
            </Row>
        </Container>
    )
}
