import { Container, Col, Input, Form, InputGroup, InputGroupAddon, Button, Row,Modal,ModalHeader,ModalBody ,ModalFooter} from 'reactstrap';
import '../App.css';

function SearchBox(props) {
    return (
        <Row className="searchBox">
            <Col xs="12">
                <Form className="inputBox" onChange={props.handleSearchInputChange} onSubmit={props.handleSearch}>
                    <InputGroup  >
                        <Input />
                        <InputGroupAddon addonType="append"><Button color="secondary">Search</Button></InputGroupAddon>
                    </InputGroup>
                </Form>
            </Col>
        </Row>
    )
}

function Card(props) {
    return (
        <Col xs="6" md="3" className="searchResultsCard">
            <Button onClick={event => props.toggleSearchModal()}>
                <img src={props.card.image_uris.border_crop} style={{ width: "100%" }} />
            </Button>
            <Modal isOpen={props.modalState} toggle={props.toggleSearchModal()} >
          <ModalHeader toggle={props.toggleSearchModal}>Modal title</ModalHeader>
