import React from 'react';
import { Container, Card, CardBody, CardTitle, CardSubtitle, CardText, CardHeader, Col, Input, Form, InputGroup, InputGroupAddon, Button, Row, Modal, ModalBody, ModalHeader, ModalFooter } from 'reactstrap';
import '../App.css';

function PlayerCard(props) {
    return (
        <Col xs="4">
            <Card>
                <CardHeader tag="h3"><input className="playerName" placeholder={"Player " + props.idx + 1} /></CardHeader>
                <CardBody>
                    <CardTitle>Card title</CardTitle>
                    <CardSubtitle>Card subtitle</CardSubtitle>
                    <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                    <Button>Button</Button>
                </CardBody>
            </Card>
        </Col>
    )
}

export default function BattleCounter(props) {
    return (
        <Container>
            <Button onClick={event => props.addPlayer()}>Add Player</Button>
            <Button onClick={event => props.removeAllPlayers()}>Remove All Players</Button>

            <Row>
                {props.battleCounterState.players.map((player, idx) => <PlayerCard player={player} idx={idx} />)}
            </Row>
        </Container>
    )
}
