import React from 'react';
import { Container, Col, Input, Form, InputGroup, InputGroupAddon, Button, Row } from 'reactstrap';
import '../App.css';

function SearchBox(props) {
    return (
        <Row className="searchBox">
            <Col>
                <Form className="inputBox" onChange={props.handleSearchInputChange} onSubmit={props.handleSearch}>
                    <InputGroup  >
                        <Input />
                        <InputGroupAddon addonType="append"><Button color="secondary">Search</Button></InputGroupAddon>
                    </InputGroup>
                </Form>
            </Col>
        </Row>
    )
}

function Card(props) {
    return (
        <Col xs="6" md="3">
            <p>{props.name}</p>
        </Col>
    )
}




export default function CardSearch(props) {
    return (
        <Container className="CardSearchWrapper">
            <Row>
                <SearchBox handleSearchInputChange={props.handleSearchInputChange} handleSearch={props.handleSearch} />
            </Row>
            <Row>

                {props.cardSearchState.cardList.forEach(card => <Card name={card.name} />)}
            </Row>
        </Container>

    )
}
