import React from 'react';
import { Container, Col, Input, Form, InputGroup, InputGroupAddon, Button, Row, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import '../App.css';

function SearchBox(props) {
    return (
        <Row className="searchBox">
            <Col xs="12">
                <Form className="inputBox" onChange={props.handleSearchInputChange} onSubmit={props.handleSearch}>
                    <InputGroup  >
                        <Input />
                        <InputGroupAddon addonType="append"><Button color="secondary">Search</Button></InputGroupAddon>
                    </InputGroup>
                </Form>
            </Col>
        </Row>
    )
}

function Card(props) {
    return (
        <Col xs="6" md="3" className="searchResultsCard">
            <Button onClick={event => props.toggleSearchModal()}>
                <img src={props.card.image_uris.border_crop} style={{ width: "100%" }} />
            </Button>
            <Modal isOpen={props.modalState} toggle={event => props.toggleSearchModal()} backdrop={false} >
                <ModalHeader toggle={event => props.toggleSearchModal()}>Modal title</ModalHeader>
                <ModalBody>
                    {props.card.name}
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={event => props.toggleSearchModal()}>Add to Deck</Button>{' '}
                    <Button color="secondary" onClick={event => props.toggleSearchModal()}>Cancel</Button>
                </ModalFooter>
            </Modal>
        </Col>
    )
}




export default function CardSearch(props) {
    return (
        <Container className="CardSearchWrapper">
            <SearchBox handleSearchInputChange={props.handleSearchInputChange} handleSearch={props.handleSearch} />
            <Row>
                {props.cardSearchState.cardList.map((card, idx) => <Card key={idx} card={card} toggleSearchModal={props.toggleSearchModal} modalState={props.cardSearchState.modalState} />)}
            </Row>
        </Container>

    )
}
