import React, { Component } from 'react';
import CardSearch from './pages/cardSearch'

import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      cardSearchState: {
        inputValueCardName: ''

      }
    }


    // Card Search Function 
    this.handleInputChange = this.handleInputChange.bind(this);


  }

  handleInputChange(event) {
    let cardSearch = this.state.cardSearchState;
    cardSearch.inputValueCardName = event.target.value;
    this.setState({ cardSearchState: cardSearch });
  }



  render() {
    return (
      <div className="App">
        <header className="App-header">
        </header>
        <CardSearch handleInputChange={this.handleInputChange} cardSearchState={this.state.cardSearchState} />
      </div>
    );
  }
}

export default App;
