import React from 'react';
import { Container, Col, Row, Table } from 'reactstrap';
import Loading from '../images/loading.svg';
import Black from '../images/mana symbols/b.svg'

function SetRow(props) {
    return (
        <tr className="setRow" key={props.idx} onClick={event => props.selectSet(props.set)}>
            <td style={{ paddingLeft: (props.set.set_type === "token" ? "2rem" : "") }}><img className="setIcon" src={props.set.icon_svg_uri} alt="" /></td>
            <td style={{ paddingLeft: (props.set.set_type === "token" ? "2rem" : "") }}>{props.set.name}</td>
            <td>{props.set.card_count}</td>
            <td>{props.set.released_at ? props.set.released_at : "-"}</td>
            <td>{props.set.set_type}</td>
        </tr>
    )
}

function SetTable(props) {
    return (
        <Row>
            <Col xs={{ size: "10", offset: "1" }}>
                <Table hover size="sm" className="setsTable" responsive>
                    <thead>
                        <tr>
                            <th></th>
                            <th>Set</th>
                            <th>Card Count</th>
                            <th>Release Date</th>
                            <th>Set Type</th>
                        </tr>
                    </thead>
                    <tbody>
                        {props.setSearchState.allSets.map((set, idx) => <SetRow set={set} idx={idx} key={idx} selectSet={props.selectSet} />)}
                    </tbody>
                </Table>
            </Col>
        </Row>
    )
}

export default function SetSearch(props) {
    return (
        <Container>

            <Row>
                <Col xs="3">
                    {props.setSearchState.loading ? <img src={Loading} alt="" style={{ width: "100%" }} /> : ""}
                </Col>
            </Row>
            {!props.setSearchState.loading ? <SetTable setSearchState={props.setSearchState} selectSet={props.selectSet} /> : ""}



        </Container>
    )
}
