import React from "react";

let blackMana =
    "https://uploads.codesandbox.io/uploads/user/59b2117e-1531-4ee1-b599-70276af97670/yqee-black-mana-symbol.svg",
    blueMana =
        "https://uploads.codesandbox.io/uploads/user/59b2117e-1531-4ee1-b599-70276af97670/BRcs-blue-mana-symbol.svg",
    greenMana =
        "https://uploads.codesandbox.io/uploads/user/59b2117e-1531-4ee1-b599-70276af97670/EEol-green-mana-symbol.svg",
    redMana =
        "https://uploads.codesandbox.io/uploads/user/59b2117e-1531-4ee1-b599-70276af97670/0xm8-red-mana-symbol.svg",
    whiteMana =
        "https://uploads.codesandbox.io/uploads/user/59b2117e-1531-4ee1-b599-70276af97670/yppH-white-mana-symbol.svg";

function getSymbols(text, idx) {
    switch (text) {
        case "{B}":
            return (
                <img className="manaSymbol black-mana" src={blackMana} key={idx} />
            );
            break;
        case "{U}":
            return <img className="manaSymbol blue-mana" src={blueMana} key={idx} />;
            break;
        case "{G}":
            return (
                <img className="manaSymbol green-mana" src={greenMana} key={idx} />
            );
            break;
        case "{R}":
            return <img className="manaSymbol red-mana" src={redMana} key={idx} />;
            break;
        case "{W}":
            return (
                <img className="manaSymbol white-mana" src={whiteMana} key={idx} />
            );
            break;
        default:
            return (
                <span className="manaSymbol any-mana" key={idx}>
                    {removeBrackets(text)}
                </span>
            );
    }
}
function removeBrackets(text) {
    var mana = text.split("{")[1].split("}")[0];
    return mana;
}
export default function manaCostSymbols(manaCostText) {
    var manaCostArray = manaCostText
        .split("{")
        .map(element => "{" + element)
        .filter(element => 1 < element.length);

    var manaCostSymbolic = (
        <div className="container manaCost">
            <div className="row">
                {manaCostArray.map((element, idx) => getSymbols(element, idx))}
            </div>
        </div>
    );

    return manaCostSymbolic;
}
