import React from "react";
import blackMana from '../images/mana symbols/b.svg'
import blueMana from '../images/mana symbols/u.svg'
import redMana from '../images/mana symbols/r.svg'
import whiteMana from '../images/mana symbols/w.svg'
import greenMana from '../images/mana symbols/g.svg'








function getSymbols(text, idx) {
    switch (text) {
        case "{B}":
            return (
                <img src={blackMana} key={idx} />
            );
            break;
        case "{U}":
            return <img src={blueMana} key={idx} />;
            break;
        case "{G}":
            return (
                <img src={greenMana} key={idx} />
            );
            break;
        case "{R}":
            return <img src={redMana} key={idx} />;
            break;
        case "{W}":
            return (
                <img src={whiteMana} key={idx} />
            );
            break;
        default:
            return (
                <span className="manaSymbol any-mana" key={idx}>
                    {removeBrackets(text)}
                </span>
            );
    }
}
function removeBrackets(text) {
    var mana = text.split("{")[1].split("}")[0];
    return mana;
}
export default function manaCostSymbols(manaCostText) {
    var manaCostArray = manaCostText
        .split("{")
        .map(element => "{" + element)
        .filter(element => 1 < element.length);

    var manaCostSymbolic = (
        <div className="container manaCost">
            <div className="row">
                {manaCostArray.map((element, idx) => getSymbols(element, idx))}
            </div>
        </div>
    );

    return manaCostSymbolic;
}
